import React, {useState, Fragment} from 'react';

function Cita({cita, index}) {
    return (
        <div className="cita">
            <p>Mascota: <span>{cita.mascota}</span></p>
            <p>DueÃ±o: <span>{cita.propietario}</span></p>
            <p>Fecha: <span>{cita.fecha}</span></p>
            <p>Hora: <span>{cita.hora}</span></p>
            <p>Sintomas: <span>{cita.sintomas}</span></p>
            <button
                onClick={() => eliminarCita(index)}
                type="button" className="button eliminar u-full-width">Eliminar X</button>
        </div>
    )
}

function Formulario({crearCita}) {

    const stateInicial = {
        mascota: '',
        propietario: '',
        fecha: '',
        hora: '',
        sintomas: ''
    }

    // cita = state actual
    // actualizarCita = fn para cambiar el state
    const [cita, actualizarCita] = useState(stateInicial);

    // actualiza el state
    const actualizarState = e => {
        actualizarCita({
            ...cita,
            [e.target.name]: e.target.value
        })
    }

    // pasamos la cita al componente principal
    const enviarCita = e => {
        e.preventDefault();

        console.log(cita);

        // Pasar la cita hacia el componente principal
        crearCita(cita);
        // Reiniciar el state (reiniciar el form)
        actualizarCita(stateInicial)
    }


    return (
        <Fragment>
            <h2>Crear Cita</h2>

            <form onSubmit={enviarCita}>
                <label>Nombre Mascota</label>
                <input
                    type="text"
                    name="mascota"
                    className="u-full-width"
                    placeholder="Nombre Mascota"
                    onChange={actualizarState}
                    value={cita.mascota}
                />

                <label>Nombre DueÃ±o</label>
                <input
                    type="text"
                    name="propietario"
                    className="u-full-width"
                    placeholder="Nombre DueÃ±o de la Mascota"
                    onChange={actualizarState}
                    value={cita.propietario}
                />

                <label>Fecha</label>
                <input
                    type="date"
                    className="u-full-width"
                    name="fecha"
                    onChange={actualizarState}
                    value={cita.fecha}
                />

                <label>Hora</label>
                <input
                    type="time"
                    className="u-full-width"
                    name="hora"
                    onChange={actualizarState}
                    value={cita.hora}
                />

                <label>Sintomas</label>
                <textarea
                    className="u-full-width"
                    name="sintomas"
                    onChange={actualizarState}
                    value={cita.sintomas}
                ></textarea>

                <button type="submit" className="button-primary u-full-width">Agregar</button>
            </form>
        </Fragment>
    )
}

function App() {
    // useState retorna 2 funciones
    // El state actual = this.setState;
    // FunciÃ³n que actualiza el state this.setState();
    const [citas, guardarCitas] = useState([]);

    // Agregar las nuevas citas al state
    const crearCita = cita => {
        // Tomar una copia del state y agregar el nuevo cliente
        const nuevasCitas = [...citas, cita]
        // Almacenamos en el state
        guardarCitas(nuevasCitas);
    }

    return (
        <Fragment>
            <h1>Administrador de Pacientes</h1>

            <div className="container">
                <div className="row">
                    <div className="one-half column">
                        <Formulario
                            crearCita={crearCita}
                        />
                    </div>
                    <div className="one-half column">
                        {
                            citas.map((cita, index) => (
                                <Cita
                                    key={index}
                                    index={index}
                                    cita={cita}
                                />
                            ))}
                    </div>
                </div>
            </div>
        </Fragment>

    )
}

export default App;
